    });
    
    // 2. –£–±–∏—Ä–∞–µ–º –≤—Å–µ –±–∏—Ç—ã–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è (–∫–æ—Ç–æ—Ä—ã–µ –Ω–µ –∑–∞–≥—Ä—É–∂–∞—é—Ç—Å—è)
    function hideBrokenImages() {
        document.querySelectorAll('img').forEach(function(img) {
            img.addEventListener('error', function() {
                console.log('‚ùå –ë–∏—Ç–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –Ω–∞–π–¥–µ–Ω–æ:', this.src);
                
                // –°–∫—Ä—ã–≤–∞–µ–º —Ä–æ–¥–∏—Ç–µ–ª—å—Å–∫–∏–π div —Å–ª–∞–π–¥–∞ –µ—Å–ª–∏ —ç—Ç–æ –≥–∞–ª–µ—Ä–µ—è
                let slideParent = this.closest('.rs-thumbs__slide-slide');
                if (slideParent) {
                    slideParent.style.display = 'none';
                    console.log('üö´ –°–∫—Ä—ã—Ç –≤–µ—Å—å —Å–ª–∞–π–¥ —Å –±–∏—Ç—ã–º –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ–º');
                } else {
