                    this.style.display = 'none';
                }
            });
        });
    }
    
    // 3. –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞ —á–µ—Ä–µ–∑ 500–º—Å
    setTimeout(function() {
        document.querySelectorAll('.rs-thumbs__slide-slide img, .rs-product__slider img').forEach(function(img) {
