                if (slideParent) {
                    slideParent.style.display = 'none';
                    console.log('üö´ –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ —Å–∫—Ä—ã—Ç —Å–ª–∞–π–¥:', img.src);
                }
